import { Word, GradeLevel, PhonicsPattern } from '@/types';

const CUSTOM_WORDS_STORAGE_KEY = 'spellbound_custom_words_v1';

function safeParse<T>(raw: string | null, fallback: T): T {
  if (!raw) return fallback;
  try { return JSON.parse(raw) as T; } catch { return fallback; }
}

function toAlphaWord(input: string): string {
  return input.trim().toLowerCase().replace(/[^a-z]/g, '');
}

function estimateSyllables(word: string): number {
  const w = word.toLowerCase();
  const vowels = 'aeiouy';
  let groups = 0;
  let prevVowel = false;
  for (const ch of w) {
    const isVowel = vowels.includes(ch);
    if (isVowel && !prevVowel) groups += 1;
    prevVowel = isVowel;
  }
  if (w.endsWith('e') && groups > 1 && !vowels.includes(w[w.length - 2] || '')) groups -= 1;
  return Math.max(1, Math.min(groups, 4));
}

function inferPatterns(word: string): PhonicsPattern[] {
  const w = word.toLowerCase();
  const patterns = new Set<PhonicsPattern>();

  const digraphs = ['ch','sh','th','wh','ph','ck'];
  const vowelTeams = ['ai','ay','ea','ee','ie','oa','oe','oo','ou','ow','ue','ui','oi','oy','au','aw','ew'];
  const rControlled = ['ar','er','ir','or','ur'];
  const prefixes = ['un','re','pre','dis','mis','non','inter','trans','sub','super','anti','auto'];
  const suffixes = ['ing','ed','er','est','ly','tion','sion','ment','ness','ful','less','able','ible','ous','ive','al'];

  if (w.length <= 3) patterns.add('CVC');
  if (w.length > 3 && w.endsWith('e') && !w.endsWith('ee') && !w.endsWith('oe') && !w.endsWith('ye')) patterns.add('silent-e');
  if (vowelTeams.some(v => w.includes(v))) patterns.add('vowel-teams');
  if (rControlled.some(r => w.includes(r))) patterns.add('r-controlled');
  if (digraphs.some(d => w.includes(d))) patterns.add('digraphs');
  if (/^(bl|br|cl|cr|dr|fl|fr|gl|gr|pl|pr|sl|sm|sn|sp|st|sw|tr|tw)/.test(w) || /(nd|st|mp|sk|nt|ft|lk|lp|lt|rm)/.test(w)) patterns.add('blends');
  if (prefixes.some(p => w.startsWith(p))) patterns.add('prefixes');
  if (suffixes.some(s => w.endsWith(s))) patterns.add('suffixes');

  if (patterns.size === 0) patterns.add(w.length > 4 ? 'blends' : 'CVC');

  const order: PhonicsPattern[] = ['CVC','blends','digraphs','silent-e','vowel-teams','r-controlled','prefixes','suffixes'];
  return order.filter(p => patterns.has(p));
}

const sightWords = new Set([
  'a','i','the','and','to','in','is','it','you','that','he','was','for','on','are','as','with','his','they','at',
  'be','this','have','from','or','one','had','by','but','not','what','all','were','we','when','your','can','said',
]);

function difficultyForGrade(grade: GradeLevel): number {
  if (grade === 'K') return 1;
  const n = Number(grade);
  return Number.isFinite(n) ? Math.max(1, Math.min(5, n)) : 1;
}

function makeWord(id: string, word: string, grade: GradeLevel): Word {
  const w = word.toLowerCase();
  return {
    id,
    word: w,
    grade,
    length: w.length,
    syllables: estimateSyllables(w),
    phonicsPattern: inferPatterns(w),
    isSightWord: sightWords.has(w),
    difficulty: difficultyForGrade(grade),
  };
}

