export const wordBank: Word[] = [
  ...kindergarten,
  ...grade1,
  ...grade2,
  ...grade3,
  ...grade4,
  ...grade5,
];

export function listCustomWords(): Word[] {
  if (typeof window === 'undefined') return [];
  const raw = window.localStorage.getItem(CUSTOM_WORDS_STORAGE_KEY);
  const data = safeParse<Word[]>(raw, []);
  // Ensure shape is sane and words are normalized
  return data
    .filter(w => !!w?.word && !!w?.grade)
    .map(w => makeWord(w.id || `cw-${Date.now()}`, w.word, w.grade as GradeLevel));
}

export function addCustomWord(inputWord: string, grade: GradeLevel): Word | null {
  if (typeof window === 'undefined') return null;

  const word = toAlphaWord(inputWord);
  if (!word) return null;

  const base = wordBank.find(w => w.grade === grade && w.word.toLowerCase() === word);
  const existingCustom = listCustomWords().find(w => w.grade === grade && w.word.toLowerCase() === word);
  if (base || existingCustom) return null;

  const newWord = makeWord(`cw-${grade}-${Date.now()}`, word, grade);
  const next = [...listCustomWords(), newWord];
  window.localStorage.setItem(CUSTOM_WORDS_STORAGE_KEY, JSON.stringify(next));
  return newWord;
}

export function removeCustomWord(id: string) {
  if (typeof window === 'undefined') return;
  const next = listCustomWords().filter(w => w.id !== id);
  window.localStorage.setItem(CUSTOM_WORDS_STORAGE_KEY, JSON.stringify(next));
}

export const getWordsByGrade = (grade: GradeLevel): Word[] => {
  const base = wordBank.filter(w => w.grade === grade);
  const custom = listCustomWords().filter(w => w.grade === grade);

  const seen = new Set<string>();
  const merged: Word[] = [];
  // Prefer custom words first (if any), then base, but keep unique
  for (const w of [...custom, ...base]) {
    const key = w.word.toLowerCase();
    if (!seen.has(key)) {
      seen.add(key);
      merged.push(w);
    }
  }
  return merged;
};

export const getRandomWords = (
  grade: GradeLevel,
  count: number = 10,
  filters?: {
    phonicsPatterns?: string[];
    lengthRange?: [number, number];
  }
): Word[] => {
  let words = getWordsByGrade(grade);

  if (filters?.phonicsPatterns && filters.phonicsPatterns.length > 0) {
    words = words.filter(word =>
      word.phonicsPattern.some(p => filters.phonicsPatterns?.includes(p))
    );
  }

  if (filters?.lengthRange) {
    words = words.filter(word =>
      word.length >= filters.lengthRange![0] && word.length <= filters.lengthRange![1]
    );
  }

  // Unique sampling by word text to prevent duplicates
  const seen = new Set<string>();
  const shuffled = [...words].sort(() => Math.random() - 0.5);
  const out: Word[] = [];
  for (const w of shuffled) {
    const key = w.word.toLowerCase();
    if (!seen.has(key)) {
      seen.add(key);
      out.push(w);
      if (out.length >= count) break;
    }
  }

  // If not enough (rare), just return what we have
  return out;
};
